<template>
	<view class="dxi_pic-lists" :class="[isList? 'pic-lists-row':'',myclass]" :style="{padding:`${tbPadding*2}rpx ${lrPadding*2}rpx`,background:bgColor}" @click="toClick">
		<view class="pic pr12" v-if="src"><image class="flex" :style="{width:imgWidth*2+'rpx',height:imgHeight*2+'rpx',borderRadius:imgR*2+'rpx'}" mode="aspectFill" :src="src" @click="imageClick"></image></view>
		<view class="slot"><slot></slot></view>
		<view class="pic-border" v-if="!noborder" :style="{left:borderLeft*2+'rpx',right:borderRight*2+'rpx'}"></view>
	</view>
</template>
<script>
	export default {
	components: {},
	props:{
		myclass:{
			type: String,
			default:''
		},
		isList:{
			type:Boolean,
			default: true
		},
		tbPadding:{
			type: Number,
			default: 12
		},
		lrPadding:{
			type: Number,
			default: 12
		},
		src:{
			type:String,
			deftault: ''
		},
		bgColor:{
			type:String,
			default: '#fff'
		},
		imgWidth:{
			type:Number,
			default: 80
		},
		imgHeight:{
			type:Number,
			default: 80
		},
		imgR: {
			type:  Number,
			default: 3
		},
		noborder: {
			type: Boolean,
			default: false
		},
		borderLeft:{
			type:Number,
			default:0
		},
		borderRight:{
			type:Number,
			default:0
		}
	},
	data() {
		return {
			
		}
	},
	methods: {
		imageClick(){
			return this.$emit('imageClick')
		},
		toClick(){
			return this.$emit('click')
		}
	}
}
</script>
<style>
.dxi_pic-lists{display: flex;position: relative;}
.dxi_pic-lists .slot{flex: 1;overflow: hidden;}
.pic-border{position: absolute;bottom: 0;left: 0;right: 0;height: 2upx;background-color: #eaeef1;-webkit-transform: scaleY(0.5);transform: scaleY(0.5);}
</style>