<template>
	<view class="dxi-tabs2-view" :class="[isFixed?'dxi-tabs2-fixed':'dxi-tabs2-relative',unlined?'dxi-unlined':'']"
	 :style="{padding:`${TBpadding*2}rpx ${LRpadding*2}rpx`,background:bgColor,top:isFixed?top+'px':'auto'}">
		<view class="dxi-tabs2-box" :style="{background:boxBgColor,borderRadius:itemHeight+'rpx'}">
			<view v-for="(item,index) in tabs" :key="index" class="dxi-tabs2-item" @tap.stop="swichTabs(index)"
			 :style="{width:itemWidth*2+'rpx',height:itemHeight*2+'rpx'}">
				<view class="dxi-tabs2-title" :class="{'dxi-tabs2-active':currentTab==index,'dxi-tabs2-disabled':item.disabled}" :style="{color:currentTab==index?selectedColor:color,fontSize:size+'rpx',lineHeight:size+'rpx',fontWeight:bold && currentTab==index?'bold':'normal'}">{{item.name}}</view>
			</view>
			<view class="dxi-tabs2-slider" :style="{transform:'translateX('+scrollLeft+'px)',width:itemWidth*2+'rpx',height:itemHeight*2+'rpx',borderRadius:sliderRadius,bottom:bottom,background:sliderBgColor,marginBottom:bottom=='50%'?('-'+itemHeight*2/2+'rpx'):0}"></view>
		</view>
	</view>
</template>

<script>
	export default {
		name: "dxiTabs",
		props: {
			//标签页
			tabs: {
				type: Array,
				default () {
					return []
				}
			},
			type:{
				type:Boolean,
				default: false
			},
			//px 左右padding
			LRpadding: {
				type: Number,
				default: 15
			},
			//px 上下padding
			TBpadding: {
				type: Number,
				default: 0
			},
			//背景色
			bgColor: {
				type: String,
				default: "#FFFFFF"
			},
			//标签背景色
			boxBgColor: {
				type: String,
				default: "transparent"
			},
			// 标签高度
			itemHeight:{
				type:Number,
				default: 44,
			},
			//是否固定
			isFixed: {
				type: Boolean,
				default: false
			},
			//px
			top: {
				type: Number
					// #ifndef H5
					,
				default: 0
					// #endif
					// #ifdef H5
					,
				default: 44
				// #endif
			},
			//是否去掉底部线条
			unlined: {
				type: Boolean,
				default: false
			},
			//当前选项卡
			currentTab: {
				type: Number,
				default: 0
			},
			
			//滑块宽度
			sliderWidth: {
				type: Number,
				default: 68
			},
			//滑块高度
			sliderHeight: {
				type: Number,
				default: 6
			},
			//滑块背景颜色
			sliderBgColor: {
				type: String,
				default: "#5677fc"
			},
			sliderRadius:{
				type: String,
				default: "50rpx"
			},
			//滑块bottom
			bottom: {
				type: String,
				default: "0"
			},
			//字体颜色
			color: {
				type: String,
				default: "#666"
			},
			//选中后字体颜色
			selectedColor: {
				type: String,
				default: "#5677fc"
			},
			//字体大小
			size: {
				type: Number,
				default: 28
			},
			//选中后 是否加粗 ，未选中则无效
			bold: {
				type: Boolean,
				default: false
			}
		},
		watch: {
			currentTab() {
				this.checkCor();
			}
		},
		created() {
			setTimeout(() => {
				// 高度自适应
				uni.getSystemInfo({
					success: (res) => {
						this.winWidth = res.windowWidth;
						this.checkCor()
						this.tabsWith()
					}
				});
			}, 50);
		},
		data() {
			return {
				winWidth: 0,
				scrollLeft: 0,
				itemWidth: 0,
			};
		},
		methods: {
			tabsWith:function(){
				let iWidth = (this.winWidth-this.LRpadding*2) /this.tabs.length //计算标签页宽度
				let itemWidth = iWidth
				this.itemWidth = itemWidth
			},
			checkCor: function() {
				let width = (this.winWidth-this.LRpadding*2) /this.tabs.length
				let scrollLeft = 0
				if (this.currentTab > 0) {
					scrollLeft = scrollLeft + width * this.currentTab 
				}
				this.scrollLeft = scrollLeft
				console.log(width,3333,scrollLeft)
			},
			// 点击标题切换当前页时改变样式
			swichTabs: function(index) {
				let item = this.tabs[index]
				if (item && item.disabled) return;
				if (this.currentTab == index) {
					return false;
				} else {
					this.$emit("change", {
						index: Number(index)
					})
				}
			}
		}
	}
</script>

<style>
.dxi-tabs2-view {width: 100%;box-sizing: border-box;z-index: 9999;}
.dxi-tabs2-box{display: flex;align-items: center;position: relative;}
.dxi-tabs2-relative {position: relative;}
.dxi-tabs2-fixed {position: fixed;left: 0;}
.dxi-tabs2-fixed::before,
.dxi-tabs2-relative::before {content: '';position: absolute;border-bottom: 1upx solid #eaeef1;-webkit-transform: scaleY(0.5);transform: scaleY(0.5);bottom: 0;right: 0;left: 0;}
.dxi-unlined::before {border-bottom: 0 !important}
.dxi-tabs2-item {display: flex;align-items: center;justify-content: center;}
.dxi-tabs2-disabled {opacity: .6;}
.dxi-tabs2-title {display: flex;align-items: center;justify-content: center;position: relative;z-index: 2;}
.dxi-tabs2-active {transition: all 0.15s ease-in-out;}
.dxi-tabs2-slider {position: absolute;left: 0;transition: all 0.15s ease-in-out;z-index: 0;transform: translateZ(0);}
</style>
